/* eslint-disable linebreak-style */
import express from 'express';
import { login, teams, matches, leaderboard } from './routes';

class App {
  public app: express.Express;

  constructor() {
    this.app = express();
    this.config();
    this.routes();
  }

  private config():void {
    const accessControl: express.RequestHandler = (_req, res, next) => {
      res.header('Access-Control-Allow-Origin', '*');
      res.header('Access-Control-Allow-Methods', 'GET,POST,DELETE,OPTIONS,PUT,PATCH');
      res.header('Access-Control-Allow-Headers', '*');
      next();
    };

    this.app.use(accessControl);
    this.app.use(express.json());
  }

  private routes(): void {
    this.app.use('/login', login);
    this.app.use('/teams', teams);
    this.app.use('/matches', matches);
    this.app.use('/leaderboard', leaderboard);
  }

  public start(PORT: string | number):void {
    this.app.listen(PORT, () => console.log(`Online on ${PORT}`));
  }
}

export { App };

export const { app } = new App();

export { default as login } from './login';
export { default as teams } from './teams';
export { default as matches } from './matches';
export { default as leaderboard } from './leaderboard';

import { Router } from 'express';
import controller from '../controllers/loginController';
import { validateLogin, validateToken } from '../middlewares';

const router = Router();

router.get('/validate', validateToken, controller.getUserRole);

router.post('/', validateLogin, controller.getUser);

export default router;

import { Request, Response } from 'express';
import service from '../services/loginService';
import IRequestPayload from '../interfaces/requestInterface';

export default class Login {
  static async getUser(req: Request, res: Response) {
    const { email, password } = req.body;

    const { error, code, result } = await service.getUser({ email, password });

    if (error) return res.status(code).json({ message: error });

    return res.status(200).json(result);
  }

  static async getUserRole(req: IRequestPayload, res: Response) {
    const { tokenPayload } = req;

    if (!tokenPayload) return res.status(400).json({ message: 'token not authorized' });

    const { code, result } = service.getUserRole(tokenPayload);

    return res.status(code).json(result);
  }
}

import bcrypt from 'bcryptjs';
import Users from '../database/models/users';
import { IUser, IUserCredentials } from '../interfaces/userInterface';
import jwtToken from '../helpers/jwt';

export default class Login {
  static async getUser({ email, password }: IUserCredentials) {
    const user = await Users.findOne({ where: { email } });

    if (!user) return { code: 404, error: 'User not found' };

    const isPasswordValid = await bcrypt.compare(password, user.password);

    if (!isPasswordValid) return { code: 400, error: 'Incorrect email or password' };

    const userData = {
      id: user.id,
      username: user.username,
      role: user.role,
      email: user.email,
    };

    const token = jwtToken(userData);

    return { code: 200, result: { user: userData, token } };
  }

  static getUserRole(tokenPayload: IUser) {
    return { code: 200, result: tokenPayload.role };
  }
}

import { Model, DataTypes } from 'sequelize';
