    type: DataTypes.STRING,
    allowNull: false,
  },
  password: {
    type: DataTypes.STRING,
    allowNull: false,
  },
}, {
  sequelize: db,
  modelName: 'users',
  tableName: 'users',
  underscored: true,
  timestamps: false,
});

export default Users;

import { Sequelize } from 'sequelize';
import * as config from '../config/database';

export default new Sequelize(config);

import fs from 'fs';
import jwt from 'jsonwebtoken';
import { IUser } from '../interfaces/userInterface';

const jwtConfig = { expiresIn: '30d' };

const jwtSecret = fs.readFileSync('./jwt.evaluation.key', 'utf-8');

export default function jwtToken(data: IUser) {
  return jwt.sign(data, jwtSecret, jwtConfig);
}

export function jwtDecode(token: string) {
  return jwt.verify(token, jwtSecret) as IUser;
}

import { Request, Response, NextFunction } from 'express';
import loginSchema from '../schemas/loginSchema';

export default async (req: Request, res: Response, next: NextFunction) => {
  const { email, password } = req.body;

  const { error } = loginSchema.validate({ email, password });

  if (error) {
    const [code, message] = error.message.split('|');
    return res.status(Number(code)).json({ message });
  }

  return next();
};

import joi from 'joi';

const joiObject = joi.object({
  email: joi.string().empty().required()
    .regex(/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/)
    .messages({
      'any.required': '404|Email is required',
      'string.empty': '400|All fields must be filled',
      'string.pattern.base': '401|Incorrect email or password',
    }),
  password: joi.string().min(6).empty().required()
    .messages({
      'any.required': '404|Password is required',
      'string.empty': '400|All fields must be filled',
      'string.min': '401|Incorrect email or password',
    }),
});

export default joiObject;

import { NextFunction, Response } from 'express';
import { jwtDecode } from '../helpers/jwt';
import IRequestPayload from '../interfaces/requestInterface';

export default async (req: IRequestPayload, res: Response, next: NextFunction) => {
  const { authorization } = req.headers;

  if (!authorization) return res.status(400).json({ message: 'Your request must have a token' });

  try {
    req.tokenPayload = jwtDecode(authorization);

    return next();
  } catch (error) {
    return res.status(401).json({ message: 'Your token is invalid' });
  }
};

